import React, { Component } from 'react';

class Data extends Component {
    state = { 
        loading: true,
        data: null,
     }

     async componentDidMount() {
        const url = "https://herbarium-map-server.herokuapp.com/herbarium";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({data: data});
     }

    render() { 
        return <div>
            {this.loading ? <div>loading...</div> : <div>this.state.data</div>}
        </div>;
    }
}

export default Data;
