import React, { useState, Component, useEffect } from 'react';
import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import {findTime, calCounts, calTimes, getColorFromTimesofAccess} from './functions_inuse.js';
import * as plantData from '../data/plants.json';
import Data from './data';
require('dotenv').config();

class Map extends Component {
    state = { 
        target_date: Date.parse('01/01/1900'),
        viewport: {
            width: "100vw",
            height: "80vh",
            latitude: 37.7577,
            longitude: -122.4376,
            zoom: 3
          },
        selectedPlant: null,
        plants: [],
        modified_plants: []
     }

     componentDidMount() {
        var new_plants = [];
        fetch("https://herbarium-map-server.herokuapp.com/herbarium")
        .then((response) => response.json())
        .then((data) => {
            data.slice(0,25).forEach(plant => {
                var { Access, occid, catalogNumber, country, stateProvince, county, decimalLatitude, decimalLongitude } = plant;
                var sum_count = 0
                const counts = Access.map(calTimes, this.state.target_date)
                sum_count = counts.reduce(function(a, b){
                    return a + b;
                }, 0);
                var color = getColorFromTimesofAccess(sum_count);
                var modifited_plant = {
                    "occis": occid,
                    "catalogNumber": catalogNumber,
                    "country": country,
                    "stateProvince": stateProvince,
                    "county": county,
                    "decimalLatitude": decimalLatitude,
                    "decimalLongitude": decimalLongitude,
                    "sum_count": sum_count,
                    "color": color
                }
                new_plants.push(modifited_plant)
            })
            this.setState({ modified_plants: new_plants});
            console.log(this.state.modified_plants);
        })
     }

     handleChangeTime = () => {
         this.setState({ target_date: Date.parse('01/01/2019')})
     }

     handleReset = () => {
        this.setState({ target_date: Date.parse('01/01/1900')})
     }


    render() { 
        //console.log(this.state.plants)
        const {selectedPlant} = this.state;
        return (
            <div>
                <ReactMapGL
                    {...this.state.viewport}
                    mapboxApiAccessToken = {process.env.REACT_APP_MAPBOX_TOKEN}
                    onViewportChange={(viewport) => this.setState({viewport})}
                >
                {this.state.modified_plants.map(plant => 
                    (
                    <Marker key = {plant.occid}
                            latitude={plant.decimalLatitude}
                            longitude={plant.decimalLongitude}

                    >   
                    <button className="marker" color={plant.color}
                            onClick={()=>{this.setState({selectedPlant: plant})}}>
                                <img src="" alt="plant location"/>
                            </button>
                    </Marker>
                ))}

                {selectedPlant && <Popup
                        latitude={selectedPlant.decimalLatitude}
                        longitude={selectedPlant.decimalLongitude}
                        closeButton={true}
                        closeOnClick={false}
                        onClose={() => this.setState({selectedPlant: false})}
                        anchor="top" >
                            <div>
                                <h5>occid: {selectedPlant.occid} </h5>
                                <h5>Catalog Number: {selectedPlant.catalogNumber}</h5>
                                <h5>{selectedPlant.county}, {selectedPlant.stateProvince}, {selectedPlant.country}</h5>
                            </div>
                        </Popup>}

                </ReactMapGL>
                <button onClick = {this.handleChangeTime} className="btn-secondary btn-sm sm-2">Change Time</button>
                <button onClick = {this.handleReset} className="btn-secondary btn-sm sm-2">Reset</button>
            </div>
        )
    }
}

export default Map;
